import React, { useContext, useEffect, useState } from 'react';
import { SidebarContext } from '../contexts/SidebarContext';
import { CartContext } from '../contexts/CartContext';
import { BsBag } from 'react-icons/bs'
import { Link } from 'react-router-dom';
import Logo from '../img/logo.svg'

const Header = () => {
  const [isActive, setIsActive] = useState(false)
  const { isOpen, setIsOpen } = useContext(SidebarContext)
  const { itemAmount } = useContext(CartContext)

  useEffect(() => {
    window.addEventListener('scroll', () => {
      window.scrollY > 60 ? setIsActive(true) : setIsActive(false)
    })
  })


  return <header className={`${isActive ? 'bg-red-400' : 'bg-blue-400'} fixed w-full z-10 transi`}>
    <div className='container mx-auto flex items-center justify-between h-full'>
      <Link to="/">
        <div>
          <img src={Logo} alt="/" className='w-[40px]' />
        </div>
      </Link>
      <div onClick={() => setIsOpen(!isOpen)} className='cursor-pointer flex relative max-w-[50px]'>
        <BsBag className='text-2xl' />
        <div className='bg-red-500 absolute -right-2 -bottom-2 text-[12px] w-[18px] h-[18px] text-white rounded-full flex justify-center items-centr'
        >{itemAmount}
        </div>
      </div>
    </div>
  </header>;
};

export default Header;
